import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { bookService } from '../services/bookService';
import { rentalService } from '../services/rentalService';

const [showForm, setShowForm] = useState(false);

const [formData, setFormData] = useState({
  title: '',
  author: '',
  isbn: '',
  description: '',
  genre: '',
  publishedYear: '',
  totalCopies: '',
});


export const BooksPage: React.FC = () => {
  const [books, setBooks] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [renting, setRenting] = useState<string | null>(null);
  const { user } = useContext(AuthContext) || {};
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      setLoading(true);
      const data = await bookService.getAllBooks();
      setBooks(data);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch books');
    } finally {
      setLoading(false);
    }
  };

  const handleRent = async (bookId: string) => {
    try {
      setRenting(bookId);
      const userId = (user as any)?.id;
      await rentalService.rentBook(bookId, userId);
      alert('Book rented successfully! Due in 14 days.');
      fetchBooks();
    } catch (err: any) {
      alert(err.response?.data?.message || 'Failed to rent book');
    } finally {
      setRenting(null);
    }
  };

  if (loading) return <div className="flex items-center justify-center h-screen"><div>Loading books...</div></div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800">ðŸ“š Available Books</h1>
        {user?.role === 'admin' && (
        <button onClick={() => setShowForm(!showForm)} className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg">
      {showForm ? 'Cancel' : '+ Add Book'}
    </button>
  )}
</div>
      {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
      {books.length === 0 ? (
        <div className="text-center text-gray-600 py-12">No books available</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {books.map((book) => (
            <div key={book._id} className="bg-white rounded-lg shadow-md hover:shadow-lg p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-2">{book.title}</h3>
              <p className="text-gray-600 mb-1"><strong>Author:</strong> {book.author}</p>
              <p className="text-gray-600 mb-1"><strong>Genre:</strong> {book.genre}</p>
              <div className="flex items-center justify-between mt-4">
                <span className={`font-bold ${book.availableCopies > 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {book.availableCopies > 0 ? `${book.availableCopies} Available` : 'Not Available'}
                </span>
                {book.availableCopies > 0 && user?.role === 'customer' && (
                  <button
                    onClick={() => handleRent(book._id)}
                    disabled={renting === book._id}
                    className="bg-green-500 hover:bg-green-600 disabled:opacity-50 text-white font-bold py-2 px-4 rounded"
                  >
                    {renting === book._id ? 'Renting...' : 'Rent Book'}
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
