import React, { useEffect, useState } from 'react';
import { userService } from '../services/userService';
import { rentalService } from '../services/rentalService';
import { BackButton } from '../components/layout/BuckButton';

export const UsersPage: React.FC = () => {
  const [users, setUsers] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'customer' as 'customer' | 'admin',
  });

  const [selectedUserRentals, setSelectedUserRentals] = useState<any[] | null>(null);
  const [selectedUserName, setSelectedUserName] = useState<string>('');
  const [rentalsLoading, setRentalsLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const data = await userService.getAllUsers();
      setUsers(data);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.password) {
      alert('Please fill all fields');
      return;
    }

    try {
      await userService.createUser({
        name: formData.name,
        email: formData.email,
        password: formData.password,
        role: formData.role,
      });

      alert('User created successfully');
      setFormData({ name: '', email: '', password: '', role: 'customer' });
      setShowForm(false);
      fetchUsers();
    } catch (err: any) {
      alert(err.response?.data?.message || 'Failed to create user');
    }
  };

  const handleDeleteUser = async (userId: string) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await userService.deleteUser(userId);
        alert('User deleted successfully');
        fetchUsers();
      } catch (err: any) {
        alert(err.response?.data?.message || 'Failed to delete user');
      }
    }
  };

  const handleViewRentals = async (userId: string, name: string) => {
    try {
      setRentalsLoading(true);
      setSelectedUserName(name);
      const data = await rentalService.getRentalsForUserAsAdmin(userId);
      setSelectedUserRentals(data);
    } catch (err: any) {
      alert(err.response?.data?.message || 'Failed to fetch rentals for user');
    } finally {
      setRentalsLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl text-gray-600">Loading users...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <BackButton to="/dashboard" />

      <div className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800">ðŸ‘¥ User Management</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg"
        >
          {showForm ? 'Cancel' : '+ Add User'}
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {showForm && (
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Create New User</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-gray-700 font-semibold mb-2">Name</label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter user name"
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">Email</label>
              <input
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter email"
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">Password</label>
              <input
                type="password"
                value={formData.password}
                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter password"
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">Role</label>
              <select
                value={formData.role}
                onChange={(e) =>
                  setFormData({ ...formData, role: e.target.value as 'customer' | 'admin' })
                }
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="customer">Customer</option>
                <option value="admin">Admin</option>
              </select>
            </div>

            <button
              type="submit"
              className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg"
            >
              Create User
            </button>
          </form>
        </div>
      )}

      <div className="bg-white rounded-lg shadow-lg overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-200">
            <tr>
              <th className="px-6 py-3 text-left font-bold text-gray-800">Name</th>
              <th className="px-6 py-3 text-left font-bold text-gray-800">Email</th>
              <th className="px-6 py-3 text-left font-bold text-gray-800">Role</th>
              <th className="px-6 py-3 text-left font-bold text-gray-800">Status</th>
              <th className="px-6 py-3 text-left font-bold text-gray-800">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((u) => (
              <tr key={u._id} className="border-b hover:bg-gray-50">
                <td className="px-6 py-4">{u.name}</td>
                <td className="px-6 py-4">{u.email}</td>
                <td className="px-6 py-4">
                  <span
                    className={`px-3 py-1 rounded-full text-sm font-semibold ${
                      u.role === 'admin'
                        ? 'bg-red-200 text-red-800'
                        : 'bg-blue-200 text-blue-800'
                    }`}
                  >
                    {u.role}
                  </span>
                </td>
                <td className="px-6 py-4">
                  {u.active ? (
                    <span className="text-green-600 font-semibold">Active</span>
                  ) : (
                    <span className="text-red-600 font-semibold">Inactive</span>
                  )}
                </td>
                <td className="px-6 py-4 space-x-2">
                  <button
                    onClick={() => handleViewRentals(u._id, u.name)}
                    className="bg-gray-500 hover:bg-gray-600 text-white font-bold py-1 px-3 rounded text-sm"
                  >
                    Rentals
                  </button>
                  <button
                    onClick={async () => {
                      const newName = window.prompt('New name:', u.name);
                      if (!newName) return;
                      try {
                        await userService.updateUser(u._id, { name: newName });
                        alert('User updated');
                        fetchUsers();
                      } catch (err: any) {
                        alert(err.response?.data?.message || 'Failed to update user');
                      }
                    }}
                    className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-1 px-3 rounded text-sm"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteUser(u._id)}
                    className="bg-red-500 hover:bg-red-600 text-white font-bold py-1 px-3 rounded text-sm"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {selectedUserRentals && (
        <div className="mt-8 bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">
            Rentals for {selectedUserName}
          </h2>
          {rentalsLoading ? (
            <p className="text-gray-600">Loading rentals...</p>
          ) : selectedUserRentals.length === 0 ? (
            <p className="text-gray-600">No rentals for this user.</p>
          ) : (
            <div className="space-y-3">
              {selectedUserRentals.map((r: any) => (
                <div
                  key={r._id}
                  className="bg-gray-50 rounded-lg p-4 border-l-4 border-blue-500"
                >
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-bold text-gray-800">
                        {r.bookId?.title || 'Book'}
                      </h3>
                      <p className="text-gray-600 text-sm">
                        Status: {r.status} | Rented:{' '}
                        {new Date(r.rentedAt).toLocaleDateString()}
                        {r.dueAt &&
                          ` | Due: ${new Date(r.dueAt).toLocaleDateString()}`}
                        {r.returnedAt &&
                          ` | Returned: ${new Date(r.returnedAt).toLocaleDateString()}`}
                      </p>
                    </div>
                    <span className="text-sm font-semibold text-gray-700">
                      {r.bookId?.author}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};
