import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

export const Dashboard: React.FC = () => {
  const { user, logout } = useContext(AuthContext) || {};
  const navigate = useNavigate();

  const handleLogout = () => {
    logout?.();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <header className="bg-white shadow">
        <nav className="container mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center space-x-2">
            <span className="text-3xl">ðŸ“š</span>
            <h1 className="text-2xl font-bold text-gray-800">Book Rental System</h1>
          </div>
          <div className="flex items-center space-x-4">
            <span className="text-gray-700">Welcome, <strong>{(user as any)?.name}</strong></span>
            {(user as any)?.role === 'admin' && (
              <span className="bg-red-500 text-white px-3 py-1 rounded-full text-sm font-semibold">Admin</span>
            )}
            <button onClick={handleLogout} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded-lg">
              Logout
            </button>
          </div>
        </nav>
      </header>

      <div className="container mx-auto px-4 py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          <div className="bg-white rounded-lg shadow-lg hover:shadow-xl transition p-6 cursor-pointer" onClick={() => navigate('/books')}>
            <div className="text-5xl mb-4">ðŸ“š</div>
            <h2 className="text-2xl font-bold text-gray-800 mb-2">Browse Books</h2>
            <p className="text-gray-600">View and rent available books</p>
          </div>
          <div className="bg-white rounded-lg shadow-lg hover:shadow-xl transition p-6 cursor-pointer" onClick={() => navigate('/rentals')}>
            <div className="text-5xl mb-4">ðŸŽ«</div>
            <h2 className="text-2xl font-bold text-gray-800 mb-2">My Rentals</h2>
            <p className="text-gray-600">View your rentals</p>
          </div>
          {(user as any)?.role === 'admin' && (
            <div className="bg-white rounded-lg shadow-lg hover:shadow-xl transition p-6 cursor-pointer" onClick={() => navigate('/users')}>
              <div className="text-5xl mb-4">ðŸ‘¥</div>
              <h2 className="text-2xl font-bold text-gray-800 mb-2">Manage Users</h2>
              <p className="text-gray-600">Manage user accounts</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
