import React, { useState, useEffect } from 'react';
import { bookService } from '../../services/bookService';
import { BookCard } from '../../components/customer/BookCard';

interface Book {
  id: string;
  title: string;
  author: string;
  isbn: string;
  genre: string;
  availableCopies: number;
  totalCopies: number;
  description: string;
  coverImage?: string;
}

export const BooksPage: React.FC = () => {
  const [books, setBooks] = useState<Book[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [genreFilter, setGenreFilter] = useState('');

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await bookService.getAllBooks();
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredBooks = books.filter(book => {
    const matchesSearch = book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      book.author.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesGenre = !genreFilter || book.genre === genreFilter;
    return matchesSearch && matchesGenre;
  });

  const genres = [...new Set(books.map(book => book.genre))];

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading books...</div>;
  }

  return (
    // src/pages/customer/BooksPage.tsx
    // inside return, replace wrapper with:
    <div className="max-w-7xl mx-auto">
      <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-6">Available Books</h1>

      <div className="mb-6 grid grid-cols-1 sm:grid-cols-3 gap-3">
        <input
          type="text"
          placeholder="Search by title or authorâ€¦"
          className="col-span-2 px-3 py-2 rounded-lg border-slate-300 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          className="px-3 py-2 rounded-lg border-slate-300 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          value={genreFilter}
          onChange={(e) => setGenreFilter(e.target.value)}
        >
          <option value="">All genres</option>
          {genres.map((g) => (
            <option key={g} value={g}>{g}</option>
          ))}
        </select>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {filteredBooks.map((book) => (
          <BookCard key={book.id} book={book} />
        ))}
      </div>
    </div>
  );
};
