import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { bookService } from '../services/bookService';
import { rentalService } from '../services/rentalService';
import { BackButton } from '../components/layout/BuckButton';

export const BooksPage: React.FC = () => {
  const [books, setBooks] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [renting, setRenting] = useState<string | null>(null);
  const { user } = useContext(AuthContext) || {};
  const navigate = useNavigate();

  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    isbn: '',
    description: '',
    genre: '',
    publishedYear: '',
    totalCopies: '',
  });

  useEffect(() => {
    fetchBooks();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchBooks = async () => {
    try {
      setLoading(true);
      const data = await bookService.getAllBooks();
      setBooks(data);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch books');
    } finally {
      setLoading(false);
    }
  };

  const handleRent = async (bookId: string) => {
    try {
      setRenting(bookId);
      const userId = (user as any)?.id;
      await rentalService.rentBook(bookId, userId);
      alert('Book rented successfully! Due in 14 days.');
      fetchBooks();
    } catch (err: any) {
      alert(err.response?.data?.message || 'Failed to rent book');
    } finally {
      setRenting(null);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl text-gray-600">Loading books...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800">ðŸ“š Available Books</h1>
        {user?.role === 'admin' && (
          <button
            onClick={() => setShowForm(!showForm)}
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg"
          >
            {showForm ? 'Cancel' : '+ Add Book'}
          </button>
        )}
      </div>

      {/* Add Book Form (Admin) */}
      {showForm && user?.role === 'admin' && (
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Create New Book</h2>
          <form
            onSubmit={async (e) => {
              e.preventDefault();
              try {
                await bookService.createBook({
                  title: formData.title,
                  author: formData.author,
                  isbn: formData.isbn,
                  description: formData.description,
                  genre: formData.genre,
                  publishedYear: Number(formData.publishedYear),
                  totalCopies: Number(formData.totalCopies),
                });
                alert('Book created successfully');
                setFormData({
                  title: '',
                  author: '',
                  isbn: '',
                  description: '',
                  genre: '',
                  publishedYear: '',
                  totalCopies: '',
                });
                setShowForm(false);
                fetchBooks();
              } catch (err: any) {
                alert(err.response?.data?.message || 'Failed to create book');
              }
            }}
            className="grid grid-cols-1 md:grid-cols-2 gap-4"
          >
            <div>
              <label className="block text-gray-700 font-semibold mb-2">Title</label>
              <input
                type="text"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">Author</label>
              <input
                type="text"
                value={formData.author}
                onChange={(e) => setFormData({ ...formData, author: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">ISBN</label>
              <input
                type="text"
                value={formData.isbn}
                onChange={(e) => setFormData({ ...formData, isbn: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">Genre</label>
              <input
                type="text"
                value={formData.genre}
                onChange={(e) => setFormData({ ...formData, genre: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">Published Year</label>
              <input
                type="number"
                value={formData.publishedYear}
                onChange={(e) =>
                  setFormData({ ...formData, publishedYear: e.target.value })
                }
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-semibold mb-2">Total Copies</label>
              <input
                type="number"
                min={1}
                value={formData.totalCopies}
                onChange={(e) =>
                  setFormData({ ...formData, totalCopies: e.target.value })
                }
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="md:col-span-2">
              <label className="block text-gray-700 font-semibold mb-2">Description</label>
              <textarea
                value={formData.description}
                onChange={(e) =>
                  setFormData({ ...formData, description: e.target.value })
                }
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows={3}
              />
            </div>

            <div className="md:col-span-2 flex justify-end">
              <button
                type="submit"
                className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-6 rounded-lg"
              >
                Save Book
              </button>
            </div>
          </form>
        </div>
      )}

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {books.length === 0 ? (
        <div className="text-center text-gray-600 py-12">
          <p className="text-xl">No books available</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {books.map((book) => (
            <div key={book._id} className="bg-white rounded-lg shadow-md hover:shadow-lg p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-2">{book.title}</h3>
              <p className="text-gray-600 mb-1">
                <strong>Author:</strong> {book.author}
              </p>
              <p className="text-gray-600 mb-1">
                <strong>Genre:</strong> {book.genre}
              </p>
              <p className="text-gray-600 mb-1">
                <strong>Published:</strong> {book.publishedYear}
              </p>
              <p className="text-gray-600 mb-1">
                <strong>ISBN:</strong> {book.isbn}
              </p>
              {book.description && (
                <p className="text-gray-700 mb-4 text-sm">{book.description}</p>
              )}

              <div className="flex items-center justify-between">
                <span
                  className={`font-bold ${
                    book.availableCopies > 0 ? 'text-green-600' : 'text-red-600'
                  }`}
                >
                  {book.availableCopies > 0
                    ? `${book.availableCopies} Available`
                    : 'Not Available'}
                </span>

                {book.availableCopies > 0 && user?.role === 'customer' && (
                  <button
                    onClick={() => handleRent(book._id)}
                    disabled={renting === book._id}
                    className="bg-green-500 hover:bg-green-600 disabled:opacity-50 text-white font-bold py-2 px-4 rounded"
                  >
                    {renting === book._id ? 'Renting...' : 'Rent Book'}
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
